import React, { useState } from 'react';
import { Button, Container, Grid, TextField,  } from '@mui/material';
import { isEditable } from '@testing-library/user-event/dist/utils';


interface IngredientsFormFunction{
    parentSetArray: Function
}

function IngredientsForm(props: IngredientsFormFunction){

    const [ingredientsList, setIngredientsList] = useState<string[]>([]);
    const [newIngredient, setNewIngredient] = useState(""); 


    function handleAddIngredient(ingredient: string){

        var csv = ingredient.split(",")

        console.log("ings" + ingredientsList);
        if(newIngredient.length > 0){

            var newList = [...ingredientsList, ...csv]
            setIngredientsList(newList);
            props.parentSetArray(newList);

            setNewIngredient("");
        }
    }

    function handleRemoveIngredient(event: React.MouseEvent<HTMLButtonElement> ){

        var index = parseInt(event.currentTarget.id);
        var newList = [...ingredientsList]
        newList.splice(index, 1);
        setIngredientsList(newList);

    }

    var i = -1;
    var output = (
        <Container key={"" + ingredientsList}>
            
            <TextField id="newIngredient" value= {newIngredient} helperText={"Enter ingredients one at a time or comma separated"} onChange={(e) =>
                setNewIngredient(e.currentTarget.value)
            }/>

            <Button style={styles.addRemove} id="addIngredient" variant="contained" onClick={() => {
                handleAddIngredient(newIngredient);
            }}> + </Button>

            
            <Grid direction="row" container spacing={2}>
                {ingredientsList.map(function(ingredient) {
                    i++;
                    console.log(ingredientsList)
                    return (
                        <Grid item key={ingredient}>
                            <TextField style={styles.inputIngredient} variant="outlined" id={"" + i} value={ingredient} inputProps={{readOnly: true}}/>

                            <Button id={"" + i} style={styles.addRemove} variant="contained" onClick={(e) => {
                                handleRemoveIngredient(e);
                            }}>-</Button>

                        </Grid>
                    )
                })}
            </Grid>

        </Container>   
    )

    return output
}

const styles = ({
    addRemove:{
        backgroundColor: "#666",
        width: "44px",
        height: "24px",
        paddingLeft: "5px"
    },
    inputIngredient:{
        
    }
})


export default IngredientsForm;
