import React, { useState } from 'react';
import { Button, Container, TextField,  } from '@mui/material';
import { isEditable } from '@testing-library/user-event/dist/utils';



function IngredientsForm(){

    const [ingredientsList, setIngredientsList] = useState([""]);
    const [newIngredient, setNewIngredient] = useState(""); 


    function handleAddIngredient(ingredient: string){
        setIngredientsList(ingredientsList => [...ingredientsList, ingredient]);
    }

    var output = (
        <Container>
            
        <TextField id="newIngredient" onChange={(e) =>
            setNewIngredient(e.currentTarget.value)
        }/>

       {ingredientsList.map(function(ingredient) {
            return <TextField value={ingredient} inputProps={{readOnly: true}}/>
        })}
        
        <Button onClick={() => {
            handleAddIngredient(newIngredient);
        }}> + </Button>
    </Container>
   
    )

    return output
}



export default IngredientsForm;
