import React, { useState } from 'react';
import { Card, CircularProgress, Container, Grid } from '@mui/material';
import IngredientsForm from './components/IngredientsForm';
import SubmitForm from './components/SubmitForm';
import Recipe from './components/Recipe';

function App() {

  const [ingredientsList, setIngredientsList] = useState<string[]>([]);
  const [writtenRecipe, setWrittenRecipe] = useState<string>("");

  const [isLoading, setIsLoading] = useState();


  var output = (
    <Container>
      <Card style ={styles.headerText} > 
          RAMS-E: 
          A GPT-3 based AI recipe generator.
          100% unique, original recipes at the press of a button
      </Card>

      <Container>
        <SubmitForm ingredients={ingredientsList} setWrittenRecipe={setWrittenRecipe} setIsLoading={setIsLoading}/>
        <IngredientsForm parentSetArray={setIngredientsList}/>
      </Container>

    </Container>
  );


  var recipe;
    if(isLoading == true){
      recipe = (
        <Container style={styles.loadingText}>
          Loading... <CircularProgress />
        </Container>
      )
    }else{
      console.log("isloading false")
      recipe = (
        <Container >  
          <Recipe writtenRecipe={writtenRecipe} />
        </Container> 
      )
    }

  return (output);
  //   <>
  //   {output},
  //     {/* <Container>
  //       <Grid container spacing={1} direction="row">
  //         <Grid item>{recipe}</Grid>
  //         <Grid item>image here</Grid>
  //       </Grid>
  //     </Container> */}
  //   </>
  // );
}

const styles = ({
  headerText: {
    color: "#a65021",
    fontFamily: "Garamond",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#FFF",
    borderWidth: "2px",
    borderColor: "#000",
  },
  loadingText:{
    fontFamily: "Garamond",
    fontSize: "32px",
    alignItems: "center"
  }
})
export default App;
