import React, { useState } from 'react';
import { Container,  } from '@mui/material';
import IngredientsForm from './components/IngredientsForm';
import SubmitForm from './components/SubmitForm';
import Recipe from './components/Recipe';

function App() {

  const [ingredientsList, setIngredientsList] = useState<string[]>([]);
  const [writtenRecipe, setWrittenRecipe] = useState<string>("");

  const [isLoading, setIsLoading] = useState();


  var output = (
    <Container>
      <Container style ={styles.headerRectangle}>
        <div style={styles.headerText}> 
          <h1>RAMS-E: </h1>
          <h3>A GPT-3 based AI recipe generator. </h3>
          <h3>100% unique, original recipes at the press of a button</h3>
        </div>
      </Container>

      <Container>
        <IngredientsForm parentSetArray={setIngredientsList}/>
        <SubmitForm ingredients={ingredientsList} setWrittenRecipe={setWrittenRecipe} setIsLoading={setIsLoading}/>
      </Container>

    </Container>
  );


  var recipe;
    if(isLoading == true){
      recipe = (
        <Container>
          Loading...
        </Container>
      )
    }

  return (
    <>
    output,
    recipe
    </>
  );
}

const styles = ({
  headerText: {
    color: "#FFF",
    fontFamily: "Garamond",
  },
  headerRectangle:{
    backgroundColor: "#000",
  }
})
export default App;
